mapper.select:
SELECT COUNT(*) as cnt
FROM customer_tp as cus,
ceo_tp as ceo
WHERE cus.customer_id = #{userId}
OR ceo.ceo_id = #{userId};

SELECT COUNT(*)
FROM customer_tp
WHERE customer_id = #{userId}
AND customer_pw = #{userPw}

SELECT customer_grade
FROM customer_tp
WHERE customer_id = #{userId}

SELECT COUNT(*)
FROM ceo_tp
WHERE ceo_id = #{userId}
AND ceo_pw = #{userPw}

SELECT ceo_store
FROM ceo_tp
WHERE ceo_idx = #{ceoIdx};
